import React, { useRef, useState } from 'react';
import "./Video.css";
import VideoFooter from './VideoFooter';

function Video() {
    const [playing, setPlaying] = useState(false);
    const videoRef = useRef(null);

    const handleVideoPress = () => {
        if (playing) {
            videoRef.current.pause();
            setPlaying(false);
        } else {
            videoRef.current.play();
            setPlaying(true);
        }
    };

    return (
        <div className="video">
            <video
                onClick={handleVideoPress}
                className="video_player"
                loop
                ref={videoRef}
                src="https://player.vimeo.com/external/413882311.sd.mp4?s=c720a6b81450133a70f3034e44dce9d5316c0c04&profile_id=165&oauth2_token_id=57447761"></video>

            <VideoFooter />
            <VideoSidebar />

        </div>
    );
}

export default Video;
